package cn.cza.springboot.modules.register.Controller;import cn.cza.springboot.commons.utils.R;import cn.cza.springboot.modules.register.Service.CheckCodeService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * @Author: 车子昂 * @Date: 2019-4-25 15:23 * @Description: */@RestController@RequestMapping("/checkCode")public class CheckCodeController {    @Autowired    private CheckCodeService checkCodeService;    @PostMapping("/send/{phone}")    public R SendCheckCode(@PathVariable("phone")String phone) {       return checkCodeService.send(phone);    }}