package cn.cza.springboot.modules.register.Controller;import cn.cza.springboot.commons.utils.R;import cn.cza.springboot.modules.register.Pojo.User;import cn.cza.springboot.modules.register.Service.RegisterService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import org.springframework.web.bind.annotation.RestController;/** * @Author: 车子昂 * @Date: 2019-4-15 10:57 * @Description: */@RestController@RequestMapping("/user")public class RegisterController {    @Autowired    private RegisterService registerService;    @RequestMapping(method = RequestMethod.POST)    public R addUser(User user) {        R r = registerService.addUser(user);        return r;    }}