package cn.cza.springboot.modules.register.Service.Impl;import cn.cza.springboot.commons.utils.CheckCode;import cn.cza.springboot.commons.utils.R;import cn.cza.springboot.modules.register.Dao.UserMapper;import cn.cza.springboot.modules.register.Pojo.User;import cn.cza.springboot.modules.register.Service.CheckCodeService;import com.baomidou.mybatisplus.core.conditions.query.QueryWrapper;import com.baomidou.mybatisplus.core.conditions.update.UpdateWrapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @Author: 车子昂 * @Date: 2019-4-25 15:44 * @Description: */@Servicepublic class CheckCodeServiceImpl implements CheckCodeService {    @Autowired    private UserMapper userMapper;    @Override    public R send(String phone) {        R r = CheckCode.sendCheckCode();        UpdateWrapper<User> ew = new UpdateWrapper<>();        ew.eq("phone",phone);        User user = new User();        user.setCheckCode((String)r.get("sixRandom"));        userMapper.update(user,ew);        return r;    }}