package cn.cza.springboot.modules.register.Service.Impl;import cn.cza.springboot.commons.utils.R;import cn.cza.springboot.commons.utils.StringUtils;import cn.cza.springboot.modules.register.Dao.UserMapper;import cn.cza.springboot.modules.register.Pojo.User;import cn.cza.springboot.modules.register.Service.RegisterService;import cn.cza.springboot.modules.register.utils.QueryUserRequest;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @Author: 车子昂 * @Date: 2019-4-16 9:43 * @Description: */@Servicepublic class RegisterServiceImpl implements RegisterService {    private Logger logger = LoggerFactory.getLogger(Class.class);    @Autowired    private UserMapper userMapper;    @Override    public R addUser(User user) {        user.setId(StringUtils.get32UUID());        int insert = userMapper.insert(user);        if (insert != 0) {            return R.error(000, "新增用户成功");        } else {            return R.error(999, "插入错误");        }    }    @Override    public R getUsers(int page, int size, QueryUserRequest params) {        R r = new R();        List<User> userList = userMapper.selectUsersByPage((page-1)*size, size,params);        int total = userMapper.selectAll(params);        logger.info("111111111");        r.put("list",userList);        r.put("total",total);        return r;    }}