package cn.cza.springboot.modules.register.Service.Impl;import cn.cza.springboot.commons.utils.R;import cn.cza.springboot.commons.utils.StringUtils;import cn.cza.springboot.modules.register.Dao.UserMapper;import cn.cza.springboot.modules.register.Pojo.User;import cn.cza.springboot.modules.register.Service.RegisterService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * @Author: 车子昂 * @Date: 2019-4-16 9:43 * @Description: */@Servicepublic class RegisterServiceImpl implements RegisterService {    @Autowired    private UserMapper userMapper;    @Override    public R addUser(User user) {        user.setId(StringUtils.get32UUID());        int insert = userMapper.insert(user);        if(insert!=0){            return R.error(000,"新增用户成功");        }else {            return R.error(999,"插入错误");        }    }}