package cn.cza.springboot.modules.sys.controller;import cn.cza.springboot.modules.sys.pojo.SysUserEntity;import org.apache.shiro.SecurityUtils;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * @Author: 车子昂 * @Date: 2018/12/3 0003 10:58 * @Description: */public class AbstractController {    protected Logger logger = LoggerFactory.getLogger(getClass());    protected SysUserEntity getUser(){        return (SysUserEntity) SecurityUtils.getSubject().getPrincipal();    }    protected Long getUserId() {        return getUser().getUserId();    }}