package cn.cza.springboot.modules.sys.controller;import cn.cza.springboot.common.utils.R;import cn.cza.springboot.modules.sys.pojo.SysMenuEntity;import cn.cza.springboot.modules.sys.service.ShiroService;import cn.cza.springboot.modules.sys.service.SysMenuService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;import java.util.Set;/** * @Author: 车子昂 * @Date: 2018/12/3 0003 11:10 * @Description: */@RestController@RequestMapping("/sys/menu")public class SysMenuController extends AbstractController {    @Autowired    private SysMenuService sysMenuService;    @Autowired    private ShiroService shiroService;    /**     * 用户菜单列表     */    @RequestMapping("/user")    public R user(){        List<SysMenuEntity> menuList = sysMenuService.getUserMenuList(getUserId());        Set<String> permissions = shiroService.getUserPermissions(getUserId());        return R.ok().put("menuList", menuList).put("permissions", permissions);    }}